import React, { useState } from 'react';
import { DropzoneAreaBase } from 'react-dropzone';
import { Document, Page } from 'react-pdf';


const [pdfFile, setPdfFile] = useState(null);

const handleFileUpload = (files) => {
  setPdfFile(files[0]);
};


const renderPDF = () => {
  if (pdfFile) {
    return (
      <Document file={pdfFile}>
        <Page pageNumber={1} />
      </Document>
    );
  }
  return <div>No PDF selected</div>;
};

function App() {
  return (<DropzoneAreaBase
    acceptedFiles={['application/pdf']}
    filesLimit={1}
    onAdd={handleFileUpload}
    onDelete={() => setPdfFile(null)}
  />
  )
}

export default App;
